        /// The multiple zone ID can be retrieved by setting `multi` to "true" in the data source `alicloud.getZones`.
        /// </summary>
        [Input("zoneId")]
        public Input<string>? ZoneId { get; set; }

        /// <summary>
        /// The region ID of the secondary instance if you create a secondary instance.
        /// </summary>
        [Input("zoneIdSlaveA")]
        public Input<string>? ZoneIdSlaveA { get; set; }

        /// <summary>
        /// The region ID of the log instance if you create a log instance.
        /// </summary>
        [Input("zoneIdSlaveB")]
        public Input<string>? ZoneIdSlaveB { get; set; }

        public DdrInstanceState()
        {
        }
        public static new DdrInstanceState Empty => new DdrInstanceState();
    }
